import React, { useEffect, useRef, useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaAward } from 'react-icons/fa';
import { SiAmazonaws } from 'react-icons/si';
import IconCloud from "@/components/magicui/icon-cloud";
import profileImage from '../../assets/images/profile.webp';
import awsDeveloperLogo from '../../assets/images/awsdeveloper.png';
import awsCloudPractitionerLogo from '../../assets/images/awspractioner.png';
import './About.css';

const slugs = [
  "figma", "html5", "css3", "javascript", "react", "django", "java", "amazonaws",
  "visualstudiocode", "git", "vercel", "express", "androidstudio", "redux",
  "python", "mysql", "mongodb", "firebase", "sass"
];

const certifications = [
  {
    title: "AWS Certified Developer â€“ Associate",
    description: "Proficiency in developing, deploying, and debugging cloud-based applications using AWS.",
    icon: <SiAmazonaws size={40} />,
    logo: awsDeveloperLogo,
  },
  {
    title: "AWS Certified Cloud Practitioner",
    description: "Comprehensive understanding of AWS Cloud concepts, services, security, architecture, pricing, and support.",
    icon: <SiAmazonaws size={40} />,
    logo: awsCloudPractitionerLogo,
  },
];

const skills = [
  { category: "Front-End Development", items: ["HTML", "CSS", "JavaScript", "React"] },
  { category: "Back-End Development", items: ["Django", "Express.js", "Java"] },
  { category: "Cloud Computing", items: ["AWS services"] },
  { category: "Development Tools", items: ["Git", "VS Code", "Android Studio", "Vercel"] },
  { category: "Design", items: ["Figma"] },
];

const fadeInVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8 } },
};

const AboutSection = () => {
  const [ref, inView] = useInView({ threshold: 0.1, triggerOnce: true });

  return (
    <motion.section
      ref={ref}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
      variants={fadeInVariants}
      className="about-section"
    >
      <h2 className="section-title">About Me</h2>
      <div className="about-content">
        <img src={profileImage} alt="Chirag" className="profile-image" />
        <div className="about-text">
          <p>
            Hey there! I'm Chirag, a dedicated full-stack web developer with a passion for cloud technologies. Graduating from Sahyadri College with a degree in Information Science and Engineering, I'm equipped with the skills and knowledge to tackle the digital world's challenges.
          </p>
          <ul>
            <li>Mastering AWS certifications to stay ahead in cloud computing</li>
            <li>Debugging code swiftly and efficiently</li>
            <li>Pushing boundaries through intense problem-solving sessions</li>
            <li>Writing JavaScript and perfecting Python scripts</li>
          </ul>
        </div>
      </div>
    </motion.section>
  );
};

const CertificationSection = () => {
  const [ref, inView] = useInView({ threshold: 0.1, triggerOnce: true });

  return (
    <motion.section
      ref={ref}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
      variants={fadeInVariants}
      className="certification-section"
    >
      <h2 className="section-title">Certifications</h2>
      <div className="certifications">
        {certifications.map((cert, index) => (
          <div key={index} className="certification card">
            <div className="certification-header">
              <img src={cert.logo} alt={cert.title} className="certification-logo" />
              <h3>{cert.title}</h3>
            </div>
            <p>{cert.description}</p>
          </div>
        ))}
      </div>
    </motion.section>
  );
};

const SkillsSection = () => {
  const [ref, inView] = useInView({ threshold: 0.1, triggerOnce: true });
  const [activeTab, setActiveTab] = useState('cloud');

  return (
    <motion.section
      ref={ref}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
      variants={fadeInVariants}
      className="skills-section"
    >
      <h2 className="section-title">Skills</h2>
      <div className="skills-tabs">
        <button 
          className={`tab-button ${activeTab === 'cloud' ? 'active' : ''}`} 
          onClick={() => setActiveTab('cloud')}
        >
          Cloud
        </button>
        <button 
          className={`tab-button ${activeTab === 'list' ? 'active' : ''}`} 
          onClick={() => setActiveTab('list')}
        >
          List
        </button>
      </div>
      {activeTab === 'cloud' && (
        <div className="icon-cloud-container">
          <IconCloud iconSlugs={slugs} />
        </div>
      )}
      {activeTab === 'list' && (
        <div className="skills-list">
          {skills.map((skillGroup, index) => (
            <div key={index} className="skill-group card">
              <h3>{skillGroup.category}</h3>
              <ul>
                {skillGroup.items.map((item, itemIndex) => (
                  <li key={itemIndex}>{item}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </motion.section>
  );
};

const About = () => {
  return (
    <div className="about-container">
      <AboutSection />
      <CertificationSection />
      <SkillsSection />
    </div>
  );
};

export default About;
