import React, { useEffect, useRef } from 'react';
import './Works.css';
import narasimha from "../../assets/images/narasimha.svg";

function Works() {
  const containerRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          containerRef.current.classList.add('animate');
        }
      },
      { threshold: 0.1 }
    );

    if (containerRef.current) {
      observer.observe(containerRef.current);
    }

    return () => {
      if (containerRef.current) {
        observer.unobserve(containerRef.current);
      }
    };
  }, []);

  return (
    <div className="work-container" ref={containerRef}>
      <h2 className='companyname'>Narasimha Enterprises Hospitality</h2>
      <p className='subtitle'>A comprehensive hospitality management website</p>
      <div className='content-wrapper'>
        <div className='Narasimha-project'>
          <a href="https://narasimhaenterpriseshospitality.in/" target="_blank" rel="noopener noreferrer">
            <img className='desktop' src={narasimha} alt="Narasimha Enterprises Hospitality" />
          </a>
        </div>
        <div className='description'>
          <p>
            Developed a full-featured website for Narasimha Enterprises Hospitality, showcasing their services, team, and clients. The site includes sections for About Us, Services, Our Team, Our Clients, and Contact information.
          </p>
        </div>
      </div>
    </div>
  );
}

export default Works;
