import React, { useEffect, useRef } from 'react';
import './Home.css';
import profileImage from '../../assets/images/profile.webp'; // Replace with your image path

const Home = () => {
  const imageRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!imageRef.current) return;

      const { clientX, clientY } = e;
      const { innerWidth, innerHeight } = window;

      // Calculate the mouse position as a percentage of the window size
      const xPercent = (clientX / innerWidth - 0.5) * 2; // -1 to 1
      const yPercent = (clientY / innerHeight - 0.5) * 2; // -1 to 1

      // Apply the transformation
      imageRef.current.style.transform = `translate(${xPercent * 20}px, ${yPercent * 20}px)`;
    };

    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div className="home">
      <div className="content">
        <p className="intro">I am Chirag, a freelancer, and I like to work on projects.</p>
        <div className="background-text">
          <h1 className="fullstack">Fullstack</h1>
          <h1 className="webdeveloper">Webdeveloper</h1>
        </div>
        <div className="image-container">
          <img ref={imageRef} src={profileImage} alt="Chirag" className="profile-image" />
        </div>
        <div className="buttons">
          <button className="btn primary-btn">Download Resume</button>
          <button className="btn secondary-btn">Hire Me</button>
        </div>
      </div>
    </div>
  );
};

export default Home;
