import React from 'react';
import { motion } from 'framer-motion';
import './Works.css';
import narasimha from "../../assets/images/narasimha.svg";
import eventifyconnect from "../../assets/images/eventifyconnect.png";

const fadeInUpVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8, ease: "easeOut" } }
};

const staggerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.3
    }
  }
};

function Works() {
  return (
    <motion.div 
      className="works-container"
      initial="hidden"
      animate="visible"
      variants={staggerVariants}
    >
      <motion.div className="project" variants={fadeInUpVariants}>
        <div className="project-content">
          <motion.h2 
            className="company-name"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            Narasimha Enterprises Hospitality
          </motion.h2>
          <motion.p 
            className="subtitle"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            A comprehensive hospitality management website
          </motion.p>
          <motion.div 
            className="image-wrapper"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            <img src={narasimha} alt="Narasimha Enterprises Hospitality" className="project-image" />
          </motion.div>
          <motion.div 
            className="description"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.6 }}
          >
            <p className="description-text">
              Developed a full-featured website for Narasimha Enterprises Hospitality, showcasing their services, team, and clients. The site includes sections for:
            </p>
            <ul className="feature-list">
              <motion.li className="feature-item" whileHover={{ scale: 1.05 }}>About Us</motion.li>
              <motion.li className="feature-item" whileHover={{ scale: 1.05 }}>Services</motion.li>
              <motion.li className="feature-item" whileHover={{ scale: 1.05 }}>Our Team</motion.li>
              <motion.li className="feature-item" whileHover={{ scale: 1.05 }}>Our Clients</motion.li>
              <motion.li className="feature-item" whileHover={{ scale: 1.05 }}>Contact Information</motion.li>
            </ul>
            <motion.a 
              href="https://narasimhaenterpriseshospitality.in/" 
              target="_blank" 
              rel="noopener noreferrer"
              className="view-project-btn"
              whileHover={{ scale: 1.05, backgroundColor: "#555" }}
              whileTap={{ scale: 0.95 }}
            >
              View Project
            </motion.a>
          </motion.div>
        </div>
      </motion.div>

      {/* Repeat similar structure for Eventify Connect project */}

    </motion.div>
  );
}

export default Works;
