import React, { useState } from 'react';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="header">
      <nav>
        <h1 className="logo">
          <a href="/" title="Chirag's Portfolio">Chirag..</a>
        </h1>
        <div className={`menu-icon ${isMenuOpen ? 'open' : ''}`} onClick={toggleMenu}>
          <span></span>
          <span></span>
          <span></span>
        </div>
        <ul className={`nav-links ${isMenuOpen ? 'open' : ''}`}>
          <li><a href="/about" title="About Chirag">About</a></li>
          <li><a href="/works" title="Chirag's Works">Works</a></li>
          <li><a href="/blogs" title="Chirag's Blogs">Blogs</a></li>
          <li><a href="/contact" title="Contact Chirag" className="contact-btn">Contact Me</a></li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
