import React from 'react';
import { motion } from 'framer-motion';
import './Works.css';

const Works = () => {
  const projects = [
    {
      title: "Narasimha Enterprises Hospitality",
      description: "A comprehensive hospitality management website",
      image: "/path/to/narasimha-image.jpg",
      link: "https://narasimhaenterpriseshospitality.in/"
    },
    {
      title: "Eventify Connect",
      description: "An event management platform",
      image: "/path/to/eventify-image.jpg",
      link: "https://eventifyconnect.com/"
    }
    // Add more projects as needed
  ];

  return (
    <div className="works-container">
      <motion.h1 
        className="works-title"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        My Works
      </motion.h1>
      <div className="projects-grid">
        {projects.map((project, index) => (
          <motion.div 
            key={index}
            className="project-card"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.2 }}
          >
            <img src={project.image} alt={project.title} className="project-image" />
            <div className="project-info">
              <h2 className="project-title">{project.title}</h2>
              <p className="project-description">{project.description}</p>
              <a 
                href={project.link}
                target="_blank"
                rel="noopener noreferrer"
                className="view-project-btn"
              >
                View Project
              </a>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Works;
