import React, { useEffect, useRef } from 'react';
import './Works.css';
import narasimha from "../../assets/images/narasimha.svg";
import eventifyconnect from "../../assets/images/eventifyconnect.png";

function Works() {
  const containerRef = useRef(null);
  const narasimhaImageRef = useRef(null);
  const eventifyconnectImageRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate');
        }
      },
      { threshold: 0.1 }
    );

    if (containerRef.current) {
      observer.observe(containerRef.current);
    }

    return () => {
      if (containerRef.current) {
        observer.unobserve(containerRef.current);
      }
    };
  }, []);

  const handleMouseMove = (e, imageRef) => {
    const { left, top, width, height } = imageRef.current.getBoundingClientRect();
    const x = (e.clientX - left) / width;
    const y = (e.clientY - top) / height;

    imageRef.current.style.transform = `perspective(1000px) rotateY(${x * 5}deg) rotateX(${y * -5}deg)`;
  };

  const handleMouseLeave = (imageRef) => {
    imageRef.current.style.transform = 'perspective(1000px) rotateY(0deg) rotateX(0deg)';
  };

  return (
    <div className="work-container" ref={containerRef}>
      <div className="project">
        <h2 className='companyname'>Narasimha Enterprises Hospitality</h2>
        <p className='subtitle'>A comprehensive hospitality management website</p>
        <div className='content-wrapper'>
          <div className='Narasimha-project' 
               onMouseMove={(e) => handleMouseMove(e, narasimhaImageRef)} 
               onMouseLeave={() => handleMouseLeave(narasimhaImageRef)}>
            <a href="https://narasimhaenterpriseshospitality.in/" 
               target="_blank" 
               rel="noopener noreferrer">
              <img className='desktop' 
                   src={narasimha} 
                   alt="Narasimha Enterprises Hospitality" 
                   ref={narasimhaImageRef} />
            </a>
          </div>
          <div className='description'>
            <p>
              Developed a full-featured website for Narasimha Enterprises Hospitality, showcasing their services, team, and clients. The site includes sections for:
            </p>
            <ul>
              <li>About Us</li>
              <li>Services</li>
              <li>Our Team</li>
              <li>Our Clients</li>
              <li>Contact Information</li>
            </ul>
            <a href="https://narasimhaenterpriseshospitality.in/" 
               target="_blank" 
               rel="noopener noreferrer" 
               className="view-project-btn">
              View Project
            </a>
          </div>
        </div>
      </div>

      <div className="project">
        <h2 className='companyname'>Eventify Connect</h2>
        <p className='subtitle'>An event management platform</p>
        <div className='content-wrapper'>
          <div className='EventifyConnect-project' 
               onMouseMove={(e) => handleMouseMove(e, eventifyconnectImageRef)} 
               onMouseLeave={() => handleMouseLeave(eventifyconnectImageRef)}>
            <a href="https://eventifyconnect.com/" 
               target="_blank" 
               rel="noopener noreferrer">
              <img className='desktop' 
                   src={eventifyconnect} 
                   alt="Eventify Connect" 
                   ref={eventifyconnectImageRef} />
            </a>
          </div>
          <div className='description'>
            <p>
              Developed an event management platform for Eventify Connect, providing tools for event planning, management, and execution. The platform includes features such as:
            </p>
            <ul>
              <li>Event Scheduling</li>
              <li>Attendee Management</li>
              <li>Ticketing</li>
              <li>Analytics</li>
              <li>Contact Information</li>
            </ul>
            <a href="https://eventifyconnect.com/" 
               target="_blank" 
               rel="noopener noreferrer" 
               className="view-project-btn">
              View Project
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Works;
