import React, { useState } from 'react';
import './Works.css';

const workItems = [
  {
    id: 1,
    title: "Narasimha Enterprises Hospitality",
    description: "A comprehensive hospitality management website",
    thumbnail: "NE",
    details: "Developed a full-featured website for Narasimha Enterprises Hospitality, showcasing their services, team, and clients. The site includes sections for About Us, Services, Our Team, Our Clients, and Contact information."
  },
  // Add more work items here
];

const WorkItem = ({ item, onItemClick }) => (
  <div className="work-item" onClick={() => onItemClick(item)}>
    <div className="work-thumbnail">{item.thumbnail}</div>
    <div className="work-info">
      <h3>{item.title}</h3>
      <p>{item.description}</p>
      <button className="view-project">View Project</button>
    </div>
  </div>
);

const WorkModal = ({ item, onClose }) => (
  <div className="work-modal">
    <div className="modal-content">
      <h2>{item.title}</h2>
      <p>{item.details}</p>
      <button onClick={onClose}>Close</button>
    </div>
  </div>
);

const Works = () => {
  const [selectedWork, setSelectedWork] = useState(null);

  const handleItemClick = (item) => {
    setSelectedWork(item);
  };

  const handleCloseModal = () => {
    setSelectedWork(null);
  };

  return (
    <section id="works">
      <h2>Selected Works</h2>
      <div className="work-grid">
        {workItems.map(item => (
          <WorkItem key={item.id} item={item} onItemClick={handleItemClick} />
        ))}
      </div>
      {selectedWork && <WorkModal item={selectedWork} onClose={handleCloseModal} />}
    </section>
  );
};

export default Works;
