import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './Works.css';
import narasimha from "../../assets/images/narasimha.svg";
import eventifyconnect from "../../assets/images/eventifyconnect.png";

const projects = [
  {
    id: 1,
    name: "Narasimha Enterprises Hospitality",
    subtitle: "A comprehensive hospitality management website",
    image: narasimha,
    description: "Developed a full-featured website for Narasimha Enterprises Hospitality, showcasing their services, team, and clients.",
    features: ["About Us", "Services", "Our Team", "Our Clients", "Contact Information"],
    link: "https://narasimhaenterpriseshospitality.in/"
  },
  {
    id: 2,
    name: "Eventify Connect",
    subtitle: "An event management platform",
    image: eventifyconnect,
    description: "Developed an event management platform for Eventify Connect, providing tools for event planning, management, and execution.",
    features: ["Event Scheduling", "Attendee Management", "Ticketing", "Analytics", "Contact Information"],
    link: "https://eventifyconnect.com/"
  }
];

function Works() {
  const [selectedProject, setSelectedProject] = useState(null);

  return (
    <div className="works-container">
      <div className="project-list">
        {projects.map((project) => (
          <motion.div
            key={project.id}
            className="project-card"
            whileHover={{ scale: 1.05 }}
            onClick={() => setSelectedProject(project)}
          >
            <img src={project.image} alt={project.name} className="project-thumbnail" />
            <h3>{project.name}</h3>
          </motion.div>
        ))}
      </div>

      <AnimatePresence>
        {selectedProject && (
          <motion.div
            className="project-details"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 50 }}
          >
            <button className="close-btn" onClick={() => setSelectedProject(null)}>Ã—</button>
            <h2>{selectedProject.name}</h2>
            <p className="subtitle">{selectedProject.subtitle}</p>
            <img src={selectedProject.image} alt={selectedProject.name} className="project-image" />
            <p>{selectedProject.description}</p>
            <h4>Features:</h4>
            <ul>
              {selectedProject.features.map((feature, index) => (
                <li key={index}>{feature}</li>
              ))}
            </ul>
            <a href={selectedProject.link} target="_blank" rel="noopener noreferrer" className="view-project-btn">
              View Project
            </a>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default Works;
