// PortfolioTransition.jsx
import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import styles from 'PageTransition.css';

const PortfolioTransition = () => {
  const coverRef = useRef(null);
  const nameRef = useRef(null);
  const contentRef = useRef(null);

  useEffect(() => {
    const tl = gsap.timeline();

    tl.from(nameRef.current, {
      opacity: 0,
      x: -100,
      duration: 1,
      ease: "power2.out"
    })
    .to(nameRef.current, {
      opacity: 0,
      x: -100,
      duration: 1,
      ease: "power2.in"
    })
    .to(coverRef.current, {
      x: "-100%",
      duration: 1,
      ease: "power2.inOut"
    })
    .from(contentRef.current, {
      opacity: 0,
      y: 50,
      duration: 1,
      ease: "power2.out"
    });
  }, []);

  return (
    <div className={styles.container}>
      <div ref={coverRef} className={styles.cover}>
        <div ref={nameRef} className={styles.name}>
          <h1>Chirag</h1>
        </div>
      </div>
      <div ref={contentRef} className={styles.content}>
        <h2>Welcome to My Portfolio</h2>
        <p>Here's where you can showcase your work and skills.</p>
      </div>
    </div>
  );
};

export default PortfolioTransition;
