import React, { useEffect, useRef, useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaAward } from 'react-icons/fa';
import { SiAmazonaws } from 'react-icons/si';
import IconCloud from "@/components/magicui/icon-cloud";
import { Card, CardContent } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

const slugs = [
  "figma", "html5", "css3", "javascript", "react", "django", "java", "amazonaws",
  "visualstudiocode", "git", "vercel", "express", "androidstudio", "redux",
  "python", "mysql", "mongodb", "firebase", "sass"
];

const certifications = [
  {
    title: "AWS Certified Developer â€“ Associate",
    description: "Proficiency in developing, deploying, and debugging cloud-based applications using AWS.",
    icon: <SiAmazonaws size={40} />,
  },
  {
    title: "AWS Certified Cloud Practitioner",
    description: "Comprehensive understanding of AWS Cloud concepts, services, security, architecture, pricing, and support.",
    icon: <SiAmazonaws size={40} />,
  },
];

const skills = [
  { category: "Front-End Development", items: ["HTML", "CSS", "JavaScript", "React"] },
  { category: "Back-End Development", items: ["Django", "Express.js", "Java"] },
  { category: "Cloud Computing", items: ["AWS services"] },
  { category: "Development Tools", items: ["Git", "VS Code", "Android Studio", "Vercel"] },
  { category: "Design", items: ["Figma"] },
];

const fadeInVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8 } },
};

const AboutSection = () => {
  const [ref, inView] = useInView({ threshold: 0.1, triggerOnce: true });

  return (
    <motion.section
      ref={ref}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
      variants={fadeInVariants}
      className="mb-16"
    >
      <h2 className="text-3xl font-bold mb-6">About Me</h2>
      <div className="flex flex-col md:flex-row items-center gap-8">
        <img
          src="/path/to/profile-image.webp"
          alt="Chirag"
          className="w-64 h-64 rounded-full object-cover shadow-lg"
        />
        <div className="flex-1">
          <p className="text-lg mb-4">
            Hey there! I'm Chirag, a dedicated full-stack web developer with a passion for cloud technologies. Graduating from Sahyadri College with a degree in Information Science and Engineering, I'm equipped with the skills and knowledge to tackle the digital world's challenges.
          </p>
          <ul className="list-disc list-inside space-y-2">
            <li>Mastering AWS certifications to stay ahead in cloud computing</li>
            <li>Debugging code swiftly and efficiently</li>
            <li>Pushing boundaries through intense problem-solving sessions</li>
            <li>Writing JavaScript and perfecting Python scripts</li>
          </ul>
        </div>
      </div>
    </motion.section>
  );
};

const CertificationSection = () => {
  const [ref, inView] = useInView({ threshold: 0.1, triggerOnce: true });

  return (
    <motion.section
      ref={ref}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
      variants={fadeInVariants}
      className="mb-16"
    >
      <h2 className="text-3xl font-bold mb-6">Certifications</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {certifications.map((cert, index) => (
          <Card key={index} className="overflow-hidden">
            <CardContent className="p-6">
              <div className="flex items-center mb-4">
                {cert.icon}
                <h3 className="text-xl font-semibold ml-4">{cert.title}</h3>
              </div>
              <p>{cert.description}</p>
            </CardContent>
          </Card>
        ))}
      </div>
    </motion.section>
  );
};

const SkillsSection = () => {
  const [ref, inView] = useInView({ threshold: 0.1, triggerOnce: true });

  return (
    <motion.section
      ref={ref}
      initial="hidden"
      animate={inView ? "visible" : "hidden"}
      variants={fadeInVariants}
      className="mb-16"
    >
      <h2 className="text-3xl font-bold mb-6">Skills</h2>
      <Tabs defaultValue="cloud" className="w-full">
        <TabsList className="grid w-full grid-cols-2 lg:grid-cols-5">
          <TabsTrigger value="cloud">Cloud</TabsTrigger>
          <TabsTrigger value="list">List</TabsTrigger>
        </TabsList>
        <TabsContent value="cloud" className="mt-6">
          <div className="w-full h-[400px]">
            <IconCloud iconSlugs={slugs} />
          </div>
        </TabsContent>
        <TabsContent value="list" className="mt-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {skills.map((skillGroup, index) => (
              <Card key={index}>
                <CardContent className="p-6">
                  <h3 className="text-lg font-semibold mb-2">{skillGroup.category}</h3>
                  <ul className="list-disc list-inside">
                    {skillGroup.items.map((item, itemIndex) => (
                      <li key={itemIndex}>{item}</li>
                    ))}
                  </ul>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>
      </Tabs>
    </motion.section>
  );
};

const About = () => {
  return (
    <div className="container mx-auto px-4 py-12">
      <AboutSection />
      <CertificationSection />
      <SkillsSection />
    </div>
  );
};

export default About;
