import React from 'react';
import { motion } from 'framer-motion';
import './Works.css';

const Works = () => {
  const projects = [
    {
      title: "Narasimha Enterprises Hospitality",
      subtitle: "A comprehensive hospitality management website",
      description: "Developed a comprehensive website for Narasimha Enterprises Hospitality, highlighting their services, team, and clients. The site features dedicated sections for About Us, Services, Our Team, Our Clients, and Contact Information.",
      images: ["/path/to/narasimha-image1.jpg", "/path/to/narasimha-image2.jpg"],
      link: "https://narasimhaenterpriseshospitality.in/"
    },
    {
      title: "EventifyConnect",
      subtitle: "A event/venue booking platform",
      description: "Created an event management platform for EventifyConnect, providing tools for event planning, management, and execution. The platform includes features such as venue selection, guest management, and booking services.",
      images: ["/path/to/eventify-image.jpg"],
      link: "https://eventifyconnect.com/"
    }
  ];

  return (
    <div className="works-container">
      {projects.map((project, index) => (
        <motion.div 
          key={index}
          className="project-card"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: index * 0.2 }}
        >
          <h2 className="project-title">{project.title}</h2>
          <p className="project-subtitle">{project.subtitle}</p>
          <div className="project-images">
            {project.images.map((img, imgIndex) => (
              <img key={imgIndex} src={img} alt={`${project.title} screenshot ${imgIndex + 1}`} />
            ))}
          </div>
          <p className="project-description">{project.description}</p>
          <a href={project.link} target="_blank" rel="noopener noreferrer" className="view-project-btn">
            View Project
          </a>
        </motion.div>
      ))}
    </div>
  );
};

export default Works;
